mapper.insert:
begin
	insert into restaurant (resNo,
							  resName ,
							  bossEmail ,
							  resAddress , 
							  resTel ,
							  resKind ,
							  resPrice ,
							  resParking ,
							  resSaletime , 
							  resRest , 
							  resHoliday)
							  values(
							  	seq_restaurant.nextval
							  	,#{resname}
							  	,#{bossemail}
							  	,#{resaddress}
							  	,#{restel}
							  	,#{reskind}
							  	,#{resprice}
							  	,#{resparking}
							  	,#{ressaletime}
							  	,#{resrest}
							  	,#{resholiday});
	update account set server=1 where email=#{bossemail};
end;
